detaileddescription.para:
Examples showing how to access mesh data in an RST file. #ifdefEXAMPLE
#include"Example.h"
#else
#include<gtest/gtest.h>
#endif

#include<math.h>
#include"helpers/dpf_meshQuery.h"
#include"helpers/dpf_model.h"
#include"Plugin.h"

usingnamespaceansys::dpf;

TEST(MeshQuery,GetSolidElements)
{
std::stringfileName("../../../testfiles/mapdl_files/SolidSurfaceBeam.rst");
ansys::dpf::Modelmodel(fileName);

MeshQuerymeshQuery=model.CreateMeshQuery();

std::vector<int>ids;
ansys::dpf::DpfErrorerror;

autoelements=meshQuery.GetSolidElements(error);
elements.getIds(ids);
EXPECT_EQ(ids.size(),81);
}

