TEST(MeshQuery,GetNodesByNamedSelection)
{
std::stringfileName("../../../testfiles/Cube.rst");
ansys::dpf::Modelmodel(fileName);

MeshQuerymeshQuery=model.CreateMeshQuery();

std::vector<int>ids;
ansys::dpf::DpfErrorerror;

autonodes=meshQuery.GetNodesByNamedSelection("WHOLE_THING",error);
nodes.getIds(ids);
EXPECT_EQ(ids.size(),27);

nodes=meshQuery.GetNodesByNamedSelection("TOP",error);
nodes.getIds(ids);
EXPECT_EQ(ids.size(),9);
}

