TEST(result_info,DimensionalityNature_Homogeneity_AnalysisType_PhysicsType_unitSystem)
{
std::stringfileName("../../../testfiles/mapdl_files/TwoSolids.rst");
ansys::dpf::DataSourcesds;
ds.addResultFile(fileName);
ansys::dpf::Operatorprovider("ResultInfoProvider");
provider.connect(4,ds);

ansys::dpf::ResultInfores_info=provider.getOutputResultInfo(0);
std::stringanalysis_type_name=res_info.analysisTypeName();
EXPECT_EQ(analysis_type_name,std::string("static"));
std::stringphysics_type_name=res_info.physicsTypeName();
EXPECT_EQ(physics_type_name,std::string("mechanical"));
std::stringunit_sys_name=res_info.unitSystemName();
EXPECT_EQ(unit_sys_name,std::string("MKS:m,kg,N,s,V,A,degC"));

std::set<std::string>res=res_info.availableResults();
autoiter=res.begin();
iter++;
std::stringres_name=*iter;
ansys::dpf::Dimensionality::ENaturenat=res_info.nature(res_name);
EXPECT_EQ(nat,ansys::dpf::Dimensionality::eScalar);

//energy
res_name="Energy-stiffnessmatrix";
ansys::dpf::Unitunit_res=res_info.unit(res_name);
EXPECT_EQ(std::string(unit_res.homogeneity().c_str()),std::string("Energy"));

//disp
res_name="Displacement";
unit_res=res_info.unit(res_name);
EXPECT_EQ(std::string(unit_res.homogeneity().c_str()),std::string("Displacement"));

//Force
res_name="Force";
unit_res=res_info.unit(res_name);
EXPECT_EQ(std::string(unit_res.homogeneity().c_str()),std::string("Force"));

//Strain
res_name="Strain";
unit_res=res_info.unit(res_name);
EXPECT_EQ(std::string(unit_res.homogeneity().c_str()),std::string("DimensionLess"));

//ContactPressure
res_name="ContactPressure";
unit_res=res_info.unit(res_name);
EXPECT_EQ(std::string(unit_res.homogeneity().c_str()),std::string("Pressure"));
}

TEST(Results,readSEQV)
{
std::stringfileName("../../../testfiles/mapdl_files/TwoSolids.rst");
ansys::dpf::Modelmodel(fileName);

Resultres=model.CreateResultEvaluationWorkflow("S_eqv");

ansys::dpf::DpfErrorerror;
FieldsContainerfieldsContainer=res.EvaluateAtGivenTime(0.0,error);
EXPECT_EQ(fieldsContainer.size(),1);

Fieldfield=fieldsContainer[0];
Locationlocation=field.fieldDefinition().location();

EXPECT_EQ(field.dataSize(),344);

dp_intsize;

Operatorminmax=Operator("min_max");
minmax.connect(0,field);

FieldfieldMin=minmax.getOutputField(0);
FieldfieldMax=minmax.getOutputField(1);

autodataMax=fieldMax.data(size);
autodataMin=fieldMin.data(size);

autoids_max=fieldMax.scoping().ids(size);
autoids_min=fieldMin.scoping().ids(size);

EXPECT_DOUBLE_EQ(dataMax[0],6314.9823137518824);
EXPECT_DOUBLE_EQ(dataMin[0],62.022552581514269);

EXPECT_EQ(ids_max[0],85);
EXPECT_EQ(ids_min[0],141);
}

