EXPECT_EQ(ids_max[0],32);
EXPECT_EQ(ids_min[0],978);
}

TEST(Results,readSXAtGivenNamedSelection)
{
std::stringfileName("../../../testfiles/mapdl_files/TwoSolids.rst");
ansys::dpf::Modelmodel(fileName);

Resultres=model.CreateResultEvaluationWorkflow("SX","not_exist");
ansys::dpf::DpfErrorerror;
FieldsContainerfieldsContainer=res.EvaluateAtGivenTime(0.0,error);
EXPECT_FALSE(error.isOk());
EXPECT_EQ(fieldsContainer.size(),0);

res=model.CreateResultEvaluationWorkflow("SX","_FIXEDSU");
fieldsContainer=res.EvaluateAtGivenTime(0.0,error);
EXPECT_TRUE(error.isOk());

Fieldfield=fieldsContainer[0];
EXPECT_EQ(field.dataSize(),30);

dp_intsize;

Operatorminmax=Operator("min_max");
minmax.connect(0,field);

FieldfieldMin=minmax.getOutputField(0);
FieldfieldMax=minmax.getOutputField(1);

autodataMax=fieldMax.data(size);
autodataMin=fieldMin.data(size);

autoids_max=fieldMax.scoping().ids(size);
autoids_min=fieldMin.scoping().ids(size);

EXPECT_DOUBLE_EQ(dataMax[0],1292.4295654296875);
EXPECT_DOUBLE_EQ(dataMin[0],-2806.3163364955358);

EXPECT_EQ(ids_max[0],100);
EXPECT_EQ(ids_min[0],109);
}

