TEST(Results,readSEQVAmplitudeAtGivenStepAndSubStep)
{
std::stringfileName("../../../testfiles/complex/filetheo.rst");
ansys::dpf::Modelmodel(fileName);

TimeFreqSupporttimeFreq=model.getTimeFreqSupport();
autonumSets=timeFreq.numberOfSets();
EXPECT_EQ(numSets,10);

autonumSteps=timeFreq.numberOfSteps();
EXPECT_EQ(numSteps,1);

autonumSubSteps=timeFreq.numberOfSubStepsByStepIndex(0);
EXPECT_EQ(numSubSteps,10);

Resultres=model.CreateResultEvaluationWorkflow("S_eqv",Scoping({1},ansys::dpf::locations::nodal));

dp_intsize;

ansys::dpf::DpfErrorerror;
FieldsContaineramplitudeFieldsContainer=res.EvaluateAmplitudeAtGivenStepIndexAndSubStep(0,9,error);
EXPECT_EQ(amplitudeFieldsContainer.size(),1);
FieldamplitudeField=amplitudeFieldsContainer[0];
EXPECT_EQ(amplitudeField.dataSize(),3);
dp_doubleamplitudeValue=amplitudeField.data(size)[0];
EXPECT_DOUBLE_EQ(amplitudeValue,4823.449633735253);

//nowgettherealandimaginaryseparatelyidx0,idx9=id10
FieldsContainerfieldsContainer=res.EvaluateAtGivenStepIndexAndSubStep(0,9,error);
EXPECT_EQ(fieldsContainer.size(),2);
EXPECT_TRUE(fieldsContainer.hasLabel("complex"));
EXPECT_TRUE(fieldsContainer.hasLabel("time"));

std::vector<Field>realFields=fieldsContainer.getFieldsForTimeId(10,0);
EXPECT_EQ(realFields.size(),1);
FieldrealField=realFields.at(0);
EXPECT_EQ(realField.dataSize(),3);
dp_doublerealValue=realField.data(size)[0];
EXPECT_DOUBLE_EQ(realValue,4823.4491029746168);

std::vector<Field>imaginaryFields=fieldsContainer.getFieldsForTimeId(10,1);
EXPECT_EQ(imaginaryFields.size(),1);
FieldimaginaryField=imaginaryFields[0];
EXPECT_EQ(imaginaryField.dataSize(),3);
dp_doubleimaginaryValue=imaginaryField.data(size)[0];
EXPECT_DOUBLE_EQ(imaginaryValue,2.262784593315458);

//andcomparethem
EXPECT_DOUBLE_EQ(amplitudeValue,(dp_double)sqrt(realValue*realValue+imaginaryValue*imaginaryValue));
}

TEST(Results,readSEQVWithPhaseAtGivenTime)
{
std::stringfileName("../../../testfiles/complex/filetheo.rst");
ansys::dpf::Modelmodel(fileName);

Resultres=model.CreateResultEvaluationWorkflow("S_eqv",Scoping({1},ansys::dpf::locations::nodal));

dp_intsize;
//thetaisindegrees
constdp_doubletheta=45.0;

ansys::dpf::DpfErrorerror;
FieldsContaineramplitudeFieldsContainer=res.EvaluateWithPhaseAtGivenTime(0.0,theta,error);
EXPECT_EQ(amplitudeFieldsContainer.size(),1);
FieldamplitudeField=amplitudeFieldsContainer.at(0);
EXPECT_EQ(amplitudeField.dataSize(),3);
dp_doubleamplitudeValue=amplitudeField.data(size)[0];
EXPECT_DOUBLE_EQ(amplitudeValue,11270.644848355851);

//nowgettherealandimaginaryseparately
FieldsContainerfieldsContainer=res.EvaluateAtGivenTime(0.0,error);
EXPECT_EQ(fieldsContainer.size(),2);
EXPECT_TRUE(fieldsContainer.hasLabel("complex"));
EXPECT_TRUE(fieldsContainer.hasLabel("time"));

std::vector<Field>realFields=fieldsContainer.getFieldsForTimeId(1,0);
EXPECT_EQ(realFields.size(),1);
FieldrealField=realFields.at(0);
EXPECT_EQ(realField.dataSize(),3);
dp_doublerealValue=realField.data(size)[0];
EXPECT_DOUBLE_EQ(realValue,15953.030385790376);

std::vector<Field>imaginaryFields=fieldsContainer.getFieldsForTimeId(1,1);
EXPECT_EQ(imaginaryFields.size(),1);
FieldimaginaryField=imaginaryFields[0];
EXPECT_EQ(imaginaryField.dataSize(),3);
dp_doubleimaginaryValue=imaginaryField.data(size)[0];
EXPECT_DOUBLE_EQ(imaginaryValue,13.931584555079384);

doublethetaR=theta/180.0*3.14159265358979323846;

//andcomparethem
dp_doubleexpectedValue=realValue*cos(thetaR)-imaginaryValue*sin(thetaR);
EXPECT_DOUBLE_EQ(amplitudeValue,expectedValue);
}

TEST(Results,readRFXAtGivenScoping)
{
std::stringfilename=(unit_test_helper::unitTestPath()/"rst_operators/allKindOfComplexity.rst").string();

ansys::dpf::DataSourcesds;
ds.addResultFile(filename);

{
ansys::dpf::ModeldpfModel(ds);
std::stringresultOperator="RFX";

automesh=dpfModel.getMesh(0);
ansys::dpf::Scopingscoping=mesh.nodeScoping();
autolocation=scoping.location();
ansys::dpf::Resultres=dpfModel.CreateResultEvaluationWorkflow(resultOperator.c_str(),scoping,location);

ansys::dpf::Result*resultWorkflow=&res;
ansys::dpf::DpfErrorerr;

ansys::dpf::FieldsContainerfieldsContainer=resultWorkflow->EvaluateAtGivenTimeIndex(0,err);
EXPECT_EQ(fieldsContainer.size(),1);

ansys::dpf::Fieldfield=fieldsContainer[0];

std::vector<double>fieldData;
field.getData(fieldData);
autofd_size=fieldData.size();

EXPECT_EQ(fd_size,661);
EXPECT_NEAR(fieldData.at(0),-1.0752,1e-4);
EXPECT_NEAR(fieldData.at(300),4.9963,1e-4);
EXPECT_NEAR(fieldData.at(660),993.8184,1e-4);
}

{
ansys::dpf::ModeldpfModel(ds);
std::stringresultOperator="RFY";

automesh=dpfModel.getMesh(0);
ansys::dpf::Scopingscoping=mesh.nodeScoping();
autolocation=scoping.location();
ansys::dpf::Resultres=dpfModel.CreateResultEvaluationWorkflow(resultOperator.c_str(),scoping,location);

ansys::dpf::Result*resultWorkflow=&res;
ansys::dpf::DpfErrorerr;

ansys::dpf::FieldsContainerfieldsContainer=resultWorkflow->EvaluateAtGivenTimeIndex(0,err);
EXPECT_EQ(fieldsContainer.size(),1);

ansys::dpf::Fieldfield=fieldsContainer[0];

std::vector<double>fieldData;
field.getData(fieldData);
autofd_size=fieldData.size();

EXPECT_EQ(fd_size,661);
EXPECT_NEAR(fieldData.at(0),2.5108,1e-4);
EXPECT_NEAR(fieldData.at(300),0,1e-4);
EXPECT_NEAR(fieldData.at(660),0,1e-4);
}

{
ansys::dpf::ModeldpfModel(ds);
std::stringresultOperator="RFZ";

automesh=dpfModel.getMesh(0);
ansys::dpf::Scopingscoping=mesh.nodeScoping();
autolocation=scoping.location();
ansys::dpf::Resultres=dpfModel.CreateResultEvaluationWorkflow(resultOperator.c_str(),scoping,location);

ansys::dpf::Result*resultWorkflow=&res;
ansys::dpf::DpfErrorerr;

ansys::dpf::FieldsContainerfieldsContainer=resultWorkflow->EvaluateAtGivenTimeIndex(0,err);
EXPECT_EQ(fieldsContainer.size(),1);

ansys::dpf::Fieldfield=fieldsContainer[0];

std::vector<double>fieldData;
field.getData(fieldData);
autofd_size=fieldData.size();

EXPECT_EQ(fd_size,661);
EXPECT_NEAR(fieldData.at(0),-0.0879,1e-4);
EXPECT_NEAR(fieldData.at(300),-2.1119,1e-4);
EXPECT_NEAR(fieldData.at(660),-130.1577,1e-4);
}


}

TEST(Results,readSEQVWithPhaseAtGivenTimeIndex)
{
std::stringfileName("../../../testfiles/complex/filetheo.rst");
ansys::dpf::Modelmodel(fileName);

Resultres=model.CreateResultEvaluationWorkflow("S_eqv",Scoping({1},ansys::dpf::locations::nodal));

dp_intsize;
//thetaisindegrees
constdp_doubletheta=22.5;

ansys::dpf::DpfErrorerror;
FieldsContaineramplitudeFieldsContainer=res.EvaluateWithPhaseAtGivenTimeIndex(0,theta,error);
EXPECT_EQ(amplitudeFieldsContainer.size(),1);
FieldamplitudeField=amplitudeFieldsContainer.at(0);
EXPECT_EQ(amplitudeField.dataSize(),3);
dp_doubleamplitudeValue=amplitudeField.data(size)[1];
EXPECT_DOUBLE_EQ(amplitudeValue,14733.327201633281);

//nowgettherealandimaginaryseparately
FieldsContainerfieldsContainer=res.EvaluateAtGivenTimeIndex(0,error);
EXPECT_EQ(fieldsContainer.size(),2);
EXPECT_TRUE(fieldsContainer.hasLabel("complex"));
EXPECT_TRUE(fieldsContainer.hasLabel("time"));

std::vector<Field>realFields=fieldsContainer.getFieldsForTimeId(1,0);
EXPECT_EQ(realFields.size(),1);
FieldrealField=realFields.at(0);
EXPECT_EQ(realField.dataSize(),3);
dp_doublerealValue=realField.data(size)[1];
EXPECT_DOUBLE_EQ(realValue,15953.009290308262);

std::vector<Field>imaginaryFields=fieldsContainer.getFieldsForTimeId(1,1);
EXPECT_EQ(imaginaryFields.size(),1);
FieldimaginaryField=imaginaryFields[0];
EXPECT_EQ(imaginaryField.dataSize(),3);
dp_doubleimaginaryValue=imaginaryField.data(size)[1];
EXPECT_DOUBLE_EQ(imaginaryValue,13.932047206695039);

doublethetaR=theta/180.0*3.14159265358979323846;

//andcomparethem
dp_doubleexpectedValue=realValue*cos(thetaR)-imaginaryValue*sin(thetaR);
EXPECT_DOUBLE_EQ(amplitudeValue,expectedValue);
}

TEST(Results,readSEQVWithPhaseAtGivenStepIndexAndSubStep)
{
std::stringfileName("../../../testfiles/complex/filetheo.rst");
ansys::dpf::Modelmodel(fileName);

TimeFreqSupporttimeFreq=model.getTimeFreqSupport();
autonumSets=timeFreq.numberOfSets();
EXPECT_EQ(numSets,10);

autonumSteps=timeFreq.numberOfSteps();
EXPECT_EQ(numSteps,1);

autonumSubSteps=timeFreq.numberOfSubStepsByStepIndex(0);
EXPECT_EQ(numSubSteps,10);

Resultres=model.CreateResultEvaluationWorkflow("S_eqv",Scoping({1},ansys::dpf::locations::nodal));

dp_intsize;
//thetaisindegrees
constdp_doubletheta=67.5;

ansys::dpf::DpfErrorerror;
FieldsContaineramplitudeFieldsContainer=res.EvaluateWithPhaseAtGivenStepIndexAndSubStep(0,9,theta,error);
EXPECT_EQ(amplitudeFieldsContainer.size(),1);
FieldamplitudeField=amplitudeFieldsContainer[0];
EXPECT_EQ(amplitudeField.dataSize(),3);
dp_doubleamplitudeValue=amplitudeField.data(size)[0];
EXPECT_DOUBLE_EQ(amplitudeValue,1843.7635181923936);

//nowgettherealandimaginaryseparatelyidx0,idx9=id10
FieldsContainerfieldsContainer=res.EvaluateAtGivenStepIndexAndSubStep(0,9,error);
EXPECT_EQ(fieldsContainer.size(),2);
EXPECT_TRUE(fieldsContainer.hasLabel("complex"));
EXPECT_TRUE(fieldsContainer.hasLabel("time"));

std::vector<Field>realFields=fieldsContainer.getFieldsForTimeId(10,0);
EXPECT_EQ(realFields.size(),1);
FieldrealField=realFields.at(0);
EXPECT_EQ(realField.dataSize(),3);
dp_doublerealValue=realField.data(size)[0];
EXPECT_DOUBLE_EQ(realValue,4823.4491029746168);

std::vector<Field>imaginaryFields=fieldsContainer.getFieldsForTimeId(10,1);
EXPECT_EQ(imaginaryFields.size(),1);
FieldimaginaryField=imaginaryFields[0];
EXPECT_EQ(imaginaryField.dataSize(),3);
dp_doubleimaginaryValue=imaginaryField.data(size)[0];
EXPECT_DOUBLE_EQ(imaginaryValue,2.262784593315458);

doublethetaR=theta/180.0*3.14159265358979323846;

//andcomparethem
dp_doubleexpectedValue=realValue*cos(thetaR)-imaginaryValue*sin(thetaR);
EXPECT_DOUBLE_EQ(amplitudeValue,expectedValue);
}
